mapper.delete:
delete from article

    where article_id in

        #{id,jdbcType=INTEGER}

mapper.update:
update article
article_title = #{articleTitle,jdbcType=VARCHAR}, article_content = #{articleContent,jdbcType=LONGVARCHAR}, article_view_count = #{articleViewCount,jdbcType=INTEGER}, article_comments_count=#{articleCommentsCount,jdbcType=INTEGER}, article_status=#{article_status,jdbcType=INTEGER}, article_update_time=#{articleUpdateTime,jdbcType=TIMESTAMP}, article_summary=#{articleSummary,jdbcType=TIMESTAMP}, has_pic=#{hasPic,jdbcType=TINYINT}
where article_id=#{articleId,jdbcType=INTEGER}
