mapper.select:
select article.*, tag.* from article, tag, article_tag_ref as atr where article.article_status = 1 and article.article_id in
#{articleId}
and article.article_id = atr.article_id and tag.tag_id = atr.tag_id order by article_id DESC

mapper.delete:
delete from article

    where article_id in

        #{id,jdbcType=INTEGER}

mapper.update:
update article
article_title = #{articleTitle,jdbcType=VARCHAR}, article_content = #{articleContent,jdbcType=LONGVARCHAR}, article_view_count = #{articleViewCount,jdbcType=INTEGER}, article_comments_count=#{articleCommentsCount,jdbcType=INTEGER}, article_status=#{articleStatus,jdbcType=INTEGER}, article_update_time=#{articleUpdateTime,jdbcType=TIMESTAMP}, article_summary=#{articleSummary,jdbcType=TIMESTAMP}, has_pic=#{hasPic,jdbcType=TINYINT}, first_pic_url=#{firstPicUrl,jdbcType=VARCHAR}, is_error=#{isError,jdbcType=TINYINT}
where article_id=#{articleId,jdbcType=INTEGER}

mapper.select:
select tag.*
from tag,
     article_tag_ref as atr
where tag.tag_id = atr.tag_id
  and atr.article_id = #{articleId,jdbcType=INTEGER}
